import React from 'react'

class DisDoggo extends React.Component {

    state = {
        dog: {
            id: this.props.selectedDoggo.id,
            name: this.props.selectedDoggo.name,
            image: this.props.selectedDoggo.image,
            isGoodDog: this.props.selectedDoggo.isGoodDog,
        }
    }

    handleClick = () => {
        this.setState(prevState => ({
            dog: { 
                ...prevState.dog,
                isGoodDog: !this.state.dog.isGoodDog
            }
        }))
        this.patchDog(this.state.dog)
    }

    patchDog = (dog) => {
        console.log(dog)
        let configObj = {
            method: 'PATCH',
            header: {
                'content-type':'application/json'
            },
            body: JSON.stringify(dog)
        }

        fetch(`http://localhost:3000/pups/${this.state.dog.id}`, configObj)
        .then(resp => resp.json())
        .then(updatedDog => this.setState(prevState => {

        }))
    }



    render() {
    return (
        <div id="dog-info">
            <img src={this.props.selectedDoggo.image} alt='cutie' />
            <h2>{this.props.selectedDoggo.name}</h2>
            <button onClick={this.handleClick}>{this.state.dog.isGoodDog ? 'Good Doggo' : 'Bad Boi'}</button>
        </div>
    )
    }
}

export default DisDoggo
