import React from 'react';
import './App.css';
import DogContainer from './containers/dog-container.js'
import DisDoggo from './components/dis-doggo.js'

class App extends React.Component{

  state = {
    dogs: [],
    selectedDog: {}
  }

  selectedDog = () => {
    this.setState({
      selectedDog: this.state.dogs[0]
    })
  }

  componentDidMount() {
    fetch('http://localhost:3000/pups')
    .then(resp => resp.json())
    .then(dogs => this.setState({dogs}))
  }

  handleClick = (dog) => {
    this.setState({
      selectedDog: dog
    })
  }

  render(){
    console.log(this.state.selectedDog)
  return (
    <div className="App">
      <div id="filter-div">
        <button id="good-dog-filter">Filter good dogs: OFF</button>
      </div>
      <div>
      <DogContainer dogs={this.state.dogs} handleClick={this.handleClick}/>
      </div>
      <div id="dog-summary-container">
        <h1>DOGGO:</h1>
        <div>
        {this.state.selectedDog !== null ? <DisDoggo selectedDoggo={this.state.selectedDog} /> : null }
        </div>
      </div>
    </div>
    );
  }
}

export default App;
