class DisDoggo extends React.Component {

    state = {
        goodDog: false
    }

    handleClick = () => {
        let areTheyGood = this.props.selectedDoggo.isGoodDog
        this.setState({
            goodDog: !areTheyGood
        })
    }

    render() {
        console.log(areTheyGood)
    return (
        <div id="dog-info">
            <img src={this.props.selectedDoggo.image} alt='cutie' />
            <h2>{this.props.selectedDoggo.name}</h2>
            <button onClick={this.handleClick}>Good Doggo!</button>
        </div>
    )
    }
}

export default DisDoggo
