import React from 'react'

class DisDoggo extends React.Component {

    state = {
        id: this.props.selectedDoggo.id,
        image: this.props.selectedDoggo.image,
        goodDog: this.props.selectedDoggo.isGoodDog,
        name: this.props.selectedDoggo.name
    }

    handleClick = () => {
        this.setState({
            goodDog: !this.state.goodDog
        })
    this.patchDog()
    }

    patchDog = () => {
        let configObj = {
            method: 'PATCH',
            header: {
                'content-type':'application/json'
            },
            body: JSON.stringify(this.state.selectedDoggo)
        }

        fetch('http://localhost:3000/pups')
    }



    render() {
    return (
        <div id="dog-info">
            <img src={this.props.selectedDoggo.image} alt='cutie' />
            <h2>{this.props.selectedDoggo.name}</h2>
            <button onClick={this.handleClick}>{this.state.goodDog ? 'Good Doggo' : 'Bad Boi'}</button>
        </div>
    )
    }
}

export default DisDoggo
